% restart.
restart:-
    write('Press any key to continue.'),nl,
    get_code(_Code),
    start.


% bot_vs_bot_selector(+Option, +Size)
bot_vs_bot_selector(1, Size):-
    play_game(botvsbot-(1-1), Size), !.
bot_vs_bot_selector(2, Size):-
    play_game(botvsbot-(1-2), Size), !.
bot_vs_bot_selector(3, Size):-
    play_game(botvsbot-(2-2), Size), !.


% single_player_selector(+Option)
single_player_selector(3):- 
    cls,
    play_game(singleplayer-2, 3), !.

single_player_selector(Level):-
    board_menu(Size),
    cls,
    play_game(singleplayer-Level, Size), !.


% game_selector(+Option)
game_selector(1):-
    single_player_menu(Level),
    write(Level),
    cls,
    single_player_selector(Level).

game_selector(2):-
    board_menu(Size),
    cls,
    play_game(multiplayer, Size), !.
game_selector(3):-
    bot_vs_bot_menu(Level),
    board_menu(Size),
    bot_vs_bot_selector(Level, Size), !.

